import React from 'react'
import styled from 'styled-components'
import { ReactComponent as Arrow } from './ico_ui_arrow.svg';

const StyledArrow = styled(Arrow)`
  fill: red;
`

const StyledSelect = styled.select`
  height: 60px;
  font-size: 14px;
  color: ${props => props.theme.colors.darkBlue};
  border: 1px solid #9BABB6;
  padding-left: 20px;
  cursor: pointer;
  -webkit-appearance: none;
  -moz-appearance: none;
  appearance: none;
  text-overflow: ellipsis;
  //background: url(http://www.stackoverflow.com/favicon.ico) 96% / 15% no-repeat #ffffff;
  &:focus {
    outline: none;
  }
  &:hover, &:active {
    border-color: ${props => props.theme.colors.darkBlue};
  }
  &::-ms-expand { 
    display: none;
  }
`

const Select = ({items}) => (
    <div>
        <StyledSelect>
            {items.map(item => <option value={item}>{item}</option>)}
        </StyledSelect>
        <Arrow />
    </div>
)

export default Select
