import React, {Fragment} from 'react'
import Slider from 'react-slick'
import Question, {Title} from './components/Question'
import Select from './components/Select'
import CheckList from './components/CheckList'
import SmallCheckList from './components/SmallCheckList'
import Button from './components/Button'
import {Column, Flex} from './components/layout'
import styled from "styled-components"
import euStrany from './eu-strany';

const strany = [
    'TIP',
    'STRANA MODERNÉHO SLOVENSKA (SMS)',
    'OBYČAJNÍ ĽUDIA a nezávislé osobnosti (OĽANO - NOVA)',
    'Demokrati Slovenska - Ľudo Kaník',
    'ŠANCA',
    'SME RODINA - Boris Kollár',
    'Strana zelených Slovenska',
    'KOALÍCIA SPOLOČNE ZA SLOVENSKO',
    'MKDA-MKDSZ',
    'VZDOR',
    'MOST - HÍD',
    'SNS',
    'Odvaha - Veľká národná a proruská koalícia',
    'KSS',
    'SDKÚ-DS',
    'SMER-SD',
    'KDH',
    'SKOK!',
    'ĽS Naše Slovensko',
    '#SIEŤ',
    'SMK-MKP',
    'PD',
    'SaS',
]

const Bar = styled(Flex)`
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  height: 10px;
  background-color: ${props => props.theme.colors.lightGrey};
`

const Fill = styled(Flex)`
   transition: width 500ms;
   background-color: ${props => props.theme.colors.green};
   height: 10px;
   position: relative;
`

const ToolTip = styled.div`
  position: absolute;
  right: 0;
  top: -20px;
  transform: translate(50%, -100%);
  padding: 7px 14px;
  background-color: ${props => props.theme.colors.green};
  color: white;
  font-size: 12px;
  font-weight: bold;
  :after {
    top: 100%;
    left: 50%;
    border: solid transparent;
    content: " ";
    height: 0;
    width: 0;
    position: absolute;
    pointer-events: none;
    border-color: rgba(136, 183, 213, 0);
    border-top-color: ${props => props.theme.colors.green};;
    border-width: 8px;
    margin-left: -8px;
  }
`

const AbsoluteTitle = styled(Title)`
  position: absolute;
  left: 50%;
  transform: translate(-50%, 0);
  top: 40px;
`

const StyledSpan = styled.span`
  color: ${props => props.theme.colors.grey};
  font-size: 14px;
`

const Return = styled.p`
  font-size: 14px;
  color: ${props => props.theme.colors.grey};
  cursor: pointer;
  height: 20px;
  display: block;
  margin-top: 20px;
  text-align: center;
  margin-bottom: 0;
  &:hover {
    font-weight: bold;
  }
`

class Form extends React.Component {
    slider
    state = {
        first: null,
        second: null,
        third: null,
        fourthFirst: null,
        fourthSecond: null,
        fourthLastChanged: 2,
        fifth: null,
        sixth: null,
        seventh: null,
        currentSlide: 0,
    }
    render() {
        console.log(this.state.third, euStrany)
        const settings = {
            speed: 500,
            slidesToShow: 1,
            slidesToScroll: 1,
            draggable: false,
            arrows: false,
            accessibility: false,
            infinite: false,
            beforeChange: (current, next) => this.setState({ currentSlide: next }),
        };
        return(
            <Fragment>
                <AbsoluteTitle mb={100}>{this.state.currentSlide + 1}<StyledSpan> / 7</StyledSpan></AbsoluteTitle>
                <form action="https://docs.google.com/forms/d/e/1FAIpQLSf2YbzVtPCINoBOqCpZ2zBLDfViR5xU1OY9qsG_jH7qVMPgtg/formResponse">
                    <input hidden name="entry.1038519199" value={this.state.first} />
                    <input hidden name="second" value={this.state.second} />
                    <Column fill>
                        <Slider ref={node => { this.slider = node }} {...settings}>
                            <Question
                                title="Akú stranu ste volili v parlamentných voľbách 2016?"
                                middlePart={
                                    <Select onChange={event => this.setState({first: event.target.value})} items={strany} />
                                }
                                bottomPart={
                                    <Button disabled={!this.state.first} onClick={() => this.slider.slickNext()} />
                                }
                            />
                            <Question
                                title="Zúčastnite sa volieb do európskeho parlamentu?"
                                middlePart={
                                    <CheckList
                                        onChange={value => this.setState({second: value})}
                                        selected={this.state.second}
                                        items={['Áno', 'Ešte neviem', 'Nie']}
                                    />
                                }
                                bottomPart={
                                    <Fragment>
                                        <Button disabled={!this.state.second} onClick={() => this.slider.slickNext()} />
                                        <Return onClick={() => this.slider.slickPrev()}>
                                            Návrat na predchádzajúcu otázku
                                        </Return>
                                    </Fragment>
                                }
                            />
                            <Question
                                title="Aku stranu budete voliť?"
                                middlePart={
                                    <Select
                                        onChange={event => this.setState({third: event.target.value})}
                                        items={Object.keys(euStrany)}
                                    />
                                }
                                bottomPart={
                                    <Fragment>
                                        <Button disabled={!this.state.third} onClick={() => this.slider.slickNext()} />
                                        <Return onClick={() => this.slider.slickPrev()}>
                                            Návrat na predchádzajúcu otázku
                                        </Return>
                                    </Fragment>
                                }
                            />
                            <Question
                                title="Akych kandidatov bude kruzkovat?"
                                middlePart={<SmallCheckList
                                    onChange={value => {
                                        if (this.state.fourthSecond) {
                                            this.setState({fourthFirst: value})
                                        }
                                        if (this.state.fourthFirst) {
                                            this.setState({fourth: value})
                                        }
                                    }}
                                    items={this.state.third ? euStrany[this.state.third] : []}
                                    selected={[this.state.fourthFirst, this.state.fourthSecond]}
                                />}
                                bottomPart={
                                    <Fragment>
                                        <Button onClick={() => this.slider.slickNext()} />
                                        <Return onClick={() => this.slider.slickPrev()}>
                                            Návrat na predchádzajúcu otázku
                                        </Return>
                                    </Fragment>
                                }
                            />
                            <Question
                                title="V akom okrese bývate?"
                                middlePart={<Select items={strany} />}
                                bottomPart={
                                    <Fragment>
                                        <Button onClick={() => this.slider.slickNext()} />
                                        <Return onClick={() => this.slider.slickPrev()}>
                                            Návrat na predchádzajúcu otázku
                                        </Return>
                                    </Fragment>
                                }
                            />
                            <Question
                                title="Pohlavie"
                                middlePart={<Select items={strany} />}
                                bottomPart={
                                    <Fragment>
                                        <Button onClick={() => this.slider.slickNext()} />
                                        <Return onClick={() => this.slider.slickPrev()}>
                                            Návrat na predchádzajúcu otázku
                                        </Return>
                                    </Fragment>
                                }
                            />
                            <Question
                                title="Aké máte vzdelanie?"
                                middlePart={<Select items={strany} />}
                                bottomPart={
                                    <Fragment>
                                        <Button onClick={() => this.slider.slickNext()} last />
                                        <Return onClick={() => this.slider.slickPrev()}>
                                            Návrat na predchádzajúcu otázku
                                        </Return>
                                    </Fragment>
                                }
                            />
                        </Slider>
                    </Column>
                </form>
                <Bar>
                    <Fill width={(this.state.currentSlide + 1) / 7}>
                        {(this.state.currentSlide !== 6) && <ToolTip>{this.state.currentSlide + 1} / 7</ToolTip>}
                    </Fill>
                </Bar>
            </Fragment>
        )
    }
}

export default Form
