import React from 'react'
import styled from 'styled-components'
import { ReactComponent as Arrow } from './assets/ico_ui_arrow.svg';

const StyledArrow = styled(Arrow)`
  pointer-events: none;
  user-selecT: none;
  position: absolute;
  right: 20px;
  top: 22px;
  fill: ${props => props.theme.colors.grey};
  width: 15px;
  height: 15px;
`

const Wrapper = styled.div`
  position: relative;
  &:hover {
    ${StyledArrow} {
      fill: ${props => props.theme.colors.darkBlue};
    }
  }
`

const StyledSelect = styled.select`
  height: 60px;
  font-size: 14px;
  color: ${props => props.theme.colors.darkBlue};
  border: 1px solid ${props => props.theme.colors.grey};
  padding-left: 20px;
  padding-right: 50px;
  cursor: pointer;
  -webkit-appearance: none;
  -moz-appearance: none;
  appearance: none;
  //background: url(http://www.stackoverflow.com/favicon.ico) 96% / 15% no-repeat #ffffff;
  &:focus {
    outline: none;
  }
  &:hover, &:active {
    border-color: ${props => props.theme.colors.darkBlue};
  }
  &::-ms-expand { 
    display: none;
  }
`

const Select = ({items}) => (
    <Wrapper>
        <StyledSelect>
            {items.map(item => <option value={item}>{item}</option>)}
        </StyledSelect>
        <StyledArrow />
    </Wrapper>
)

export default Select
