import React from 'react'
import {Row, Cell, Flex} from './layout'
import styled, {css} from 'styled-components'
import {fontSize} from 'styled-system'

const Option = styled.p`
  color: ${props => props.theme.colors.darkBlue};
  margin: 0;
  line-height: 1.2;
  text-align: left;
  ${fontSize}
`

const CheckBox = styled.div`
  position: relative;
  height: 20px;
  width: 20px;
  border: 2px solid ${props => props.theme.colors.green};
  margin-right: 10px;
  flex-shrink: 0;
  ${props => props.selected && css`
    background-color: ${props => props.theme.colors.green};
  `}
  &::after {
    position: absolute;
    top: 50%;

    content: '1';
    color: white;
  }
`

const OptionWrapper = styled(Flex).attrs(() => ({height: 40, alignItems: 'center'}))`
  cursor: pointer;
`

const CheckList = ({items}) => <Row style={{width: '100%'}} justifyContent="center">
    <Cell>
        {items.slice(0, 7).map((item, i) => (
            <OptionWrapper>
                <CheckBox selected={i === 2} />
                <Option fontSize={[12, 14]}>{i + 1}.</Option>
                &nbsp;
                <Option fontSize={[12, 14]}>{item}</Option>
            </OptionWrapper>
        ))}
    </Cell>
    <Cell>
        {items.slice(7, 14).map((item, i) => (
            <OptionWrapper>
                <CheckBox />
                <Option fontSize={[12, 14]}>{i + 8}. </Option>
                &nbsp;
                <Option fontSize={[12, 14]}>{item}</Option>
            </OptionWrapper>
        ))}
    </Cell>
</Row>

export default CheckList
