import React from 'react'
import {Container, Column, Div} from './layout'
import styled from 'styled-components'
import {space} from 'styled-system'

export const Title = styled.h1`
  color: ${props => props.theme.colors.darkBlue};
  font-size: 20px;
  font-weight: bold;
  margin: 0;
  ${space}
`

const Question = ({index, title, middlePart, bottomPart}) => (
    <Container style={{height: '100vh'}}>
        <Column height="100%" alignItems="center" justifyContent="center">
            <Title>{title}</Title>
            <Column alignItems="center" style={{width: '100%', maxWidth: 600}} mt={30} mb={50}>
                {middlePart}
            </Column>
            {bottomPart}
        </Column>
    </Container>
)

export default Question
